import React from 'react';
import MagicGrid from 'magic-grid';
import MG from 'react-magic-grid';



function MGtest() {
    const Cards = ({ cards }) => (
        <MagicGrid items={cards.length}>
          {cards.map(item => (
            <Card key={item} item={item} />
          ))}
        </MagicGrid>
      )

    const addItems = (numItems=10) => {
        const container = document.querySelector(".container");
        const items = container.children;
        const lastItem = items[items.length - 1];
        const lastNumber = parseInt(lastItem.innerHTML);
      
        for (let i = 1; i <= numItems; i++){
          const newDiv = document.createElement("div");
          const newNumber = lastNumber + i;
      
          newDiv.innerText = newNumber.toString();
          container.appendChild(newDiv);
          newDiv.classList.add(`item${newNumber}`);
        }
      
        magicGrid.positionItems();
      }
      
      
      const removeItems = (numItems=10) => {
        const container = document.querySelector(".container");
        const items = container.children;
        const lastIndex = items.length - 1;
        let stopIndex = lastIndex - numItems;
      
        if (stopIndex < 0) stopIndex = 0;
      
        for (let i = lastIndex; i > stopIndex; i--){
          items[i].remove();
        }
      
        magicGrid.positionItems();
      }

    return (
        <div>
            <div class="container">
                <div class="item1">1</div>
                <div class="item2">2</div>
                <div class="item3">3</div>
                <div class="item4">4</div>
                <div class="item5">5</div>
                <div class="item6">6</div>
                <div class="item7">7</div>
                <div class="item8">8</div>
                <div class="item9">9</div>
                <div class="item10">10</div>
                <div class="item11">11</div>
                <div class="item12">12</div>
                <div class="item13">13</div>
            </div>

            <div>
                <button onclick={addItems}>Add Items</button>
                <button onclick={removeItems}>Remove Items</button>
            </div>
        </div>
    );
}

export default MGtest;
