import React, { useRef, useEffect } from "react";

import MagicGridEngine from "magic-grid";

function MagicGridPage() {
    const MagicGrid = ({ children, ...props }) => {
        const container = useRef();
        const timeout = useRef();
        const grid = useRef();
      
        const onResize = () => {
          if (!timeout.current) timeout.current = setTimeout(handleReposition, 200);
        };
      
        const handleReposition = () => {
          grid.current && grid.current.positionItems();
          clearTimeout(timeout.current);
          timeout.current = null;
        };
      
        useEffect(() => {
          if (!grid.current) {
            grid.current = new MagicGridEngine({
              container: container.current,
              items: 20,
              animate: true,
              center: true,
              static:true,
              ...props
            });
            window.addEventListener("resize", onResize);
          }
          handleReposition();
      
          return () => {
            clearTimeout(timeout.current);
            window.removeEventListener("resize", onResize);
            grid.current = null;
          };
        });
      
        useEffect(() => {
          handleReposition();
        });
        //container
        return <div ref={container}>{children}</div>;
      };
      
      //Card
      const Card = ({ name, colors }) => {
        console.log("name",name)
        return <div id="Card" style={{color:{colors}}}>{name}</div>;
      };

      let color = ["&#34;rgb(255,205,0)&#34;  ", "rgb(255, 205, 255)"]

      function agin(){
        var array = [];
        for (var i = 0; i < color.length; i++){
          array.push(<div id="Card" style={{color:{...color[i]}}}></div>)
          console.log("array",array)
          console.log("array",color[i])
        }
        return array
      }

    return (
        <div id="container">
            <div id="container">
                <MagicGrid static gutter={12}> {/*요소 간격*/}
                    <Card colors="&#34;rgb(255, 205, 0)&#34;" name="212"/>
                    <Card name="&#34;rgb(255, 205, 255)&#34;" />
                    <Card />
                    <Card />
                    <Card />
                    <Card />
                    <Card />
                    <Card />
                    {agin()}
                </MagicGrid>
            </div>
        </div>
    );
}

export default MagicGridPage;
