import React, { useRef, useEffect } from "react";

import MagicGridEngine from "magic-grid";

function MagicGridPage() {
    const MagicGrid = ({ children, ...props }) => {
      // useRef를 사용하여 children 을 제어 및 생성
      // 즉, children 에 해당하는 객체를 매직 그리드처럼 제어한다.
        const container = useRef();
        const timeout = useRef();
        const grid = useRef();
      
        // 타임 아웃 시간 조정
        const onResize = () => {
          if (!timeout.current) timeout.current = setTimeout(handleReposition, 10);
        };
      
        //재배치 (초기화 선언)
        const handleReposition = () => {
          grid.current && grid.current.positionItems();
          clearTimeout(timeout.current);
          timeout.current = null;
        };
      
        // 매직 그리드 제어
        useEffect(() => {
          if (!grid.current) {
            grid.current = new MagicGridEngine({
              container: container.current,
              items: 20,
              animate: true,
              center: true,
              static:true,
              ...props
            });
            window.addEventListener("resize", onResize);
          }
          handleReposition();
      
          // 타임 아웃
          return () => {
            clearTimeout(timeout.current);
            window.removeEventListener("resize", onResize);
            grid.current = null;
          };
        });
      
        useEffect(() => {
          handleReposition();
        });
        //container
        // 훅 함수를 사용하여 객체 생성.
        return <div ref={container}>{children}</div>;
      };

      // let colors = ["rgb(255,205,0)", "blue", "red"]

      // for (const element of colors) {
      //   const csss = {
      //     backgroundColor: [element],
      //     color: "blue"
      //   }
      // }
      
      // const csss = {
      //   backgroundColor: [colors[0]],
      //   color: "blue"
      // }
      //Card
      // 선언한 card 엘리먼트 전부 태그 값으로 출력하는 곳
      const Card = ({ name }) => {
        let colors = ["rgb(255,205,0)", "blue", "red"]

        for (const element of colors) {
          console.log("colors", element)
          return <div id="Card" style={{backgroundColor: element}}></div>;
        }
      };

    return (
        <div id="container">
            <div id="container">
                <MagicGrid static gutter={12}> {/*요소 간격*/}
                    {/*children*/}
                    <Card />
                    <Card />
                    <Card />
                    <Card />
                    <Card />
                    <Card />
                    <Card />
                    <Card />
                </MagicGrid>
            </div>
        </div>
    );
}

export default MagicGridPage;
